//https://www.hackerrank.com/challenges/common-child/

public class CommonChild {
    public static void main(String[] args) {
        System.out.println(solve("ABCD", "ABDC"));
        System.out.println(solve("HARRY", "SALLY"));
        System.out.println(solve("SHINCHAN", "NOHARAAA"));
        System.out.println(solve("FDAGCXGKCTKWNECHMRXZWMLRYUCOCZHJRRJBOAJOQJZZVUYXIC", "WEWOUCUIDGCGTRMEZEPXZFEJWISRSBBSYXAYDFEJJDLEBVHHKS"));
        System.out.println(solve("", ""));
    }

    private static int solve(String a, String b) {
        char ac[] = a.toCharArray();
        char bc[] = b.toCharArray();
        int len = a.length();
        int dp[][] = new int[len+1][len+1];
        for(int i = 1; i <= len; i++) {
            for(int j = 1; j <= len; j++) {
                if(bc[i-1] == ac[j-1]){
                    dp[i][j] = dp[i-1][j-1] +1;
                } else {
                    dp[i][j] = Math.max(dp[i][j-1], dp[i-1][j]);
                }
            }
        }
        return dp[len][len];
    }
}
